package loops;

public class Forloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		for (int d=0; d<=100; d++) {
			
			System.out.println("the value is : " +d); //Ascending order
		}
		
		for (int i=100; i>=0; i--) {
			
			System.out.println("the value is : " +i); //Descending order
		}
	}

}
